sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading && newValue === '') {		
		g_form.setSectionDisplay('related_records',false);
		g_form.setSectionDisplay('resolution_information',false);
        return;
    }	

	if(newValue === ''){		
		g_form.setSectionDisplay('related_records',false);
		g_form.setSectionDisplay('resolution_information',false);
        return;
    }	

    if (newValue === 'Issue') {
		g_form.clearOptions('state');
        g_form.setDisplay('approval', false);
        g_form.clearValue('approval');
        g_form.setDisplay('category', true);
        g_form.setVisible('category', true);
		g_form.setSectionDisplay('related_records',true);
		g_form.setSectionDisplay('resolution_information',true);
		setOption('ClientCallable','getTaskStateChoice','sys_choice','name=task^element=state','state');

    }

    if (newValue === 'Request') {
		g_form.clearOptions('state');
        g_form.setDisplay('category', false);
        g_form.clearValue('category');
        g_form.setDisplay('approval', true);
        g_form.setVisible('approval', true);
		g_form.setSectionDisplay('related_records',false);
		g_form.setSectionDisplay('resolution_information',false);
		setOption('ClientCallable','getTaskStateChoice','sys_choice','name=sc_request^element=request_state','state');
		setOption('ClientCallable','getTaskStateChoice','sys_choice','name=task^element=approval','approval');
    }
}

function setOption(className,functionName,tableName,filterQuery,fieldName)
{
	var getChoice = new GlideAjax(className);
	getChoice.addParam('sysparm_name', functionName);
	getChoice.addParam('tableName',tableName);
	getChoice.addParam('filterQuery',filterQuery);
	getChoice.getXMLAnswer(function(response) {
		var mapValue = JSON.parse(response);
		mapValue = mapValue.sort();

		for (let i = 0; i < mapValue.length; i++) {
			g_form.addOption(fieldName, mapValue[i][1], mapValue[i][1],Number(mapValue[i][0]));
		}
	});
}
