sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    g_form.setVisible('category', false);
    g_form.setVisible('approval', false);
    g_form.setDisplay('approval', false);
    g_form.setDisplay('category', false);



    if (g_form.getValue('support_type') == 'Request') {
        g_form.addInfoMessage('Inside Load Request');
        g_form.setDisplay('category', false);
        g_form.setVisible('category', false);
        g_form.setDisplay('approval', true);
        g_form.setVisible('approval', true);
		//g_form.hideRelatedList('x_1393996_tech_s_0_support_task');
		g_form.hideRelatedList('x_1393996_tech_s_0_tech_support');
		//g_form.hideRelatedList('sysapproval_approver');
        var sections = g_form.getSections();
        sections[2].style.display = '';
        sections[3].style.display = '';
		if(g_form.getValue('support_type')===''){
			stateValue = '--None--';
		}else{
        var state = g_form.getValue('state');
		}
        setOption('ClientCallable', 'getTaskStateChoice', 'sys_choice', 'name=sc_request^element=request_state', 'state',state);
        //setOption('ClientCallable', 'getTaskStateChoice', 'sys_choice', 'name=task^element=approval', 'approval',approval);
        g_form.setValue('state', state);
        g_form.setValue('approval', approval);
    }

    if (g_form.getValue('support_type') == 'Issue') {
        g_form.addInfoMessage('Inside Issue Load');
        g_form.setDisplay('approval', false);
        g_form.setVisible('approval', false);
        g_form.setDisplay('category', true);
        g_form.setVisible('category', true);
g_form.hideRelatedList('x_1393996_tech_s_0_support_task');
		//g_form.hideRelatedList('x_1393996_tech_s_0_tech_support');
		g_form.hideRelatedList('sysapproval_approver');

        var stateValue = '';

		if(g_form.getValue('support_type')==='')
		{
			stateValue = '--None--';
		}
		else
		{
			stateValue = g_form.getValue('state');
		}
        g_form.setSectionDisplay('related_records', true);
        g_form.setSectionDisplay('resolution_information', true);
        setOption('ClientCallable', 'getTaskStateChoice', 'sys_choice', 'name=task^element=state', 'state',stateValue);
        g_form.getControl('state').setAttribute('state',state);
    }

    function setOption(className, functionName, tableName, filterQuery, fieldName,defaultValue) {
        var getChoice = new GlideAjax(className);
        getChoice.addParam('sysparm_name', functionName);
        getChoice.addParam('tableName', tableName);
        getChoice.addParam('filterQuery', filterQuery);
        getChoice.getXMLAnswer(function(response) {
            var mapValue = JSON.parse(response);
            mapValue = mapValue.sort();
            var currentValue = g_form.getValue(fieldName);
			g_form.addOption(fieldName,defaultValue,defaultValue,1);
            for (var i = 0; i < mapValue.length; i++) {
                if (currentValue != mapValue[i][1]) {
                    g_form.addOption(fieldName, mapValue[i][1], mapValue[i][1], Number(mapValue[i][0]));
                }
            }
        });
    }
}
