sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (newValue.includes('Closed')) 
	{
        var getPhone = new GlideAjax('ClientCallable');
        getPhone.addParam('sysparm_name', 'getCurrentUSer');
        //getPhone.addParam('reference', g_form.getValue('requested_for'));
        getPhone.getXMLAnswer(getResponse);
        function getResponse(response) {
            var res = JSON.parse(response);
            g_form.setValue('resolved_by', res.user);
        }		
        var date = new Date();
        g_form.setValue('resolved', date.getFullYear() + '-' + ((date.getMonth()<9)?'0'+(date.getMonth()+1):(date.getMonth()+1)) + '-' + date.getDate());
		g_form.setMandatory('resolution_notes',true);
		g_form.setReadOnly('resolved_by',true);
		g_form.setReadOnly('resolved',true);
    }
	else
	{
		g_form.setMandatory('resolution_notes',false);
		g_form.setReadOnly('resolved_by',false);
		g_form.setReadOnly('resolved',false);
		g_form.setValue('resolved_by', '');
		g_form.setValue('resolved','');
		g_form.setValue('resolution_notes','');
	}
    //Type appropriate comment here, and begin script below

}
