sys_script_include.script:
var ClientCallable = Class.create();
ClientCallable.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getTaskStateChoice:function()
	{
		var tableName = this.getParameter('tableName');
		var filterQuery = this.getParameter('filterQuery');
		var task = new GlideRecord(tableName);
		task.addEncodedQuery(filterQuery);
		task.query();

		let taskChoice = new Map();
		while(task.next()){
			taskChoice.set(task.getValue('sequence').toString(),task.getValue('label').toString());
		}

		var arr =Array.from(taskChoice);
		return JSON.stringify(arr);

	},

	getContactNumber:function()
	{
		var reference = this.getParameter('reference');
		var contactNumber = new GlideRecord('sys_user');

		var data = {};
		//gs.addInfoMessage('1');
		if(contactNumber.get(reference))
		{
			data.phone_number = contactNumber.phone.toString();
			//gs.addInfoMessage(contactNumber.phone.toString());
		}
		//gs.addInfoMessage('2');
		return JSON.stringify(data);
	},

	getCurrentUSer : function()
	{
		var data = {};
		data.user = gs.getUserID();
		return JSON.stringify(data);
	},

    type: 'ClientCallable'
});
